# config.sh — Ubuntu/Debian/Linux Mint only

# -------- Distro info (Debian family only) ----------
if command -v lsb_release >/dev/null 2>&1; then
  DISTRO="$(lsb_release -is 2>/dev/null || true)"
  CODENAME="$(lsb_release -cs 2>/dev/null || true)"
  RELEASE="$(lsb_release -rs 2>/dev/null || true)"
else
  # Fallback to /etc/os-release
  if [ -r /etc/os-release ]; then
    . /etc/os-release
    DISTRO="${DISTRO:-${NAME:-}}"
    CODENAME="${CODENAME:-${VERSION_CODENAME:-}}"
    RELEASE="${RELEASE:-${VERSION_ID:-}}"
    ID="${ID:-}"
  fi
fi
RELEASE_MAJOR="${RELEASE%%.*}"

# Enforce Ubuntu/Debian/Mint only
if [ -z "${ID:-}" ]; then
  # Try to derive ID from lsb_release if needed
  ID="$(echo "${DISTRO:-}" | tr '[:upper:]' '[:lower:]' | tr -d ' ')"
fi
case "${ID:-}" in
  ubuntu|debian|linuxmint) : ;;
  *)
    echo "This starter is intended for Ubuntu/Debian/Linux Mint. Detected: ${ID:-unknown}. Exiting." >&2
    exit 1
    ;;
esac

# List of unwanted services
UNWANTED="rpcbind apache2 nginx lighttpd vsftpd proftpd pure-ftpd postfix exim4 sendmail dovecot-core cyrus-imapd courier-imap courier-pop openssh-server dropbear mysql-server mariadb-server postgresql redis-server mongodb bind9 dnsmasq unbound isc-dhcp-server kea-dhcp-server squid haproxy tinyproxy openvpn wireguard strongswan libreswan samba nfs-kernel-server slapd 389-ds snmpd rsyslog syslog-ng-core journalbeat inspircd ircd-irc2 unrealircd prosody ejabberd ntp chrony telnetd inetutils-telnetd rsync cups avahi-daemon mosquitto zabbix-server-pgsql nagios3 prometheus nis ntpdate telnetd-ssl ldap-utils rsh talk telnet snmp"

# Unwanted files
FILE_EXTENSIONS=("mp3" "mp4" "avi" "mov" "ogg" "netrc")

# Uninstall software that is not required or is considered insecure
UNWANTED_SOFTWARE="doona zenmap xprobe proxychains packit 4g8 wapiti ftpscan nbtscan pompem nmapsi4 amule pumpa fcrackzip cmospwd rfdump yersinia deluge ophcrack nmap netcat masscan zmap hping3 dnsutils smbclient arp-scan openvas nikto wpscan sqlmap john hashcat hydra medusa crowbar metasploit-framework beef-xss sqlninja wireshark tcpdump ettercap dsniff arpspoof bettercap setoolkit gophish king-phisher aircrack-ng kismet reaver fern-wifi-cracker burpsuite owasp-zap whatweb dirbuster radare2 gdb binwalk ghidra empire covenant powersploit theharvester maltego recon-ng ncrack amap socat p0f scapy hash-identifier hexedit searchsploit freeciv manaplus themole zangband hunt goldeneye"

# -------- Primary user & paths ----------
# Prefer the invoking human over root when run via sudo
PUSER="${SUDO_USER:-}"
if [ -z "$PUSER" ]; then
  PUSER="$(logname 2>/dev/null || true)"
fi
if [ -z "$PUSER" ]; then
  PUSER="${USER:-root}"
fi

# Resolve home; prefer NSS
HOME_DIR="$(getent passwd "$PUSER" | cut -d: -f6 2>/dev/null || true)"
if [ -z "$HOME_DIR" ]; then
  if [ "$PUSER" = "root" ]; then HOME_DIR="/root"; else HOME_DIR="/home/$PUSER"; fi
fi

# Where to write docs/artifacts (no side effects here; orchestrators create it)
DOCS="${DOCS:-$HOME_DIR/Desktop/System_Documentation}"

# -------- Colors (fixed ANSI; Debian family terminals handle these) ----------
BLACK='\033[0;30m'; RED='\033[0;31m'; GREEN='\033[0;32m'; YELLOW='\033[0;33m'
BLUE='\033[0;34m'; MAGENTA='\033[0;35m'; CYAN='\033[0;36m'; WHITE='\033[0;37m'
NC='\033[0m' # reset

# -------- Package manager ----------
PKG_MGR="apt"   # Debian/Ubuntu/Mint standard

# (Optional helpers elsewhere can use: sudo "$PKG_MGR" update && sudo "$PKG_MGR" -y upgrade)

